mapper.update:
UPDATE BASIC_USER
SET BASIC_USER_COIN = BASIC_USER_COIN + #{paymentCoin}
WHERE BASIC_USER_EMAIL = #{paymentUserEmail}

UPDATE PAYMENT_HISTORY
SET
    PAYMENT_STATE = #{paymentState},
    PAYMENT_METHOD = #{paymentMethod},
    PAYMENT_REFERENCE = #{paymentReference}
WHERE PAYMENT_NO = #{paymentNo}

mapper.select:
SELECT BASIC_USER_COIN
FROM BASIC_USER
WHERE BASIC_USER_EMAIL = #{userEmail}

mapper.update:
UPDATE PAYMENT_HISTORY
SET PAYMENT_STATE = 'Canceled'
WHERE PAYMENT_NO = #{paymentNo}

UPDATE BASIC_USER
SET BASIC_USER_COIN = BASIC_USER_COIN - #{userProduct}
WHERE BASIC_USER_EMAIL = #{userEmail}

UPDATE PAYMENT_HISTORY
SET PAYMENT_STATE = 'Failed'
WHERE PAYMENT_NO = #{paymentNo}
