mapper.select:
SELECT * FROM ( SELECT bu.BASIC_USER_NICKNAME , FEED_TITLE , FEED_CONTENT , FEED_INDATE , FEED_UPDATEd_DATE , FEED_TAG, FEED_NO, ROW_NUMBER() OVER (ORDER BY f.FEED_INDATE DESC) AS row_num FROM FEEDS f JOIN BASIC_USER bu ON f.AUTHOR_USER_NO = BASIC_USER_NO WHERE FEED_STATE = 'N'
-- 모든 태그 포함
AND FEED_TAG = '매칭 후기'
AND FEED_TAG = '자유 보드'
AND FEED_TAG = '연애 조언'
) WHERE row_num BETWEEN #{start} AND #{end}

SELECT COALESCE(COUNT(DISTINCT BASIC_USER_NO), 0) AS LIKE_CNT
FROM FEED_LIKES fl
WHERE FEED_NO = #{feedNo}

SELECT FEED_TITLE , FEED_CONTENT ,FEED_TAG, FEED_NO
FROM FEEDS f
WHERE FEED_NO = #{feedNo}

mapper.update:
UPDATE FEED_COMMENTS
SET COMMENT_STATE = 'Y'
WHERE COMMENT_NO = #{commentNo}

UPDATE FEEDS
SET FEED_STATE = 'Y'
WHERE FEED_NO = #{feedNo}
